def bubble_sort(arr):
    n = len(arr)
    for i in range(n):
        for j in range(0, n - i - 1):
            if arr[j] > arr[j + 1]:
                arr[j], arr[j + 1] = arr[j + 1], arr[j]

def bubble_sort_visual(arr):
    n = len(arr)
    for i in range(n):
        print(f'Pass {i + 1}: {arr}')
        swapped = False
        for j in range(0, n - i - 1):
            if arr[j] > arr[j + 1]:
                print(f'  Swapping {arr[j]} and {arr[j + 1]}')
                arr[j], arr[j + 1] = arr[j + 1], arr[j]
                swapped = True
        if not swapped:
            print('No swaps needed - array is sorted!')
            break

